pragma solidity ^0.5.0;
/**
 * The Controller is an upgradeable endpoint for controlling Folia.sol
 */

import "./X2.sol";
import "./SafeMath.sol";
import "./Ownable.sol";
import "./ReentrancyGuard.sol";
import "./IERC721.sol";
import "./IERC165.sol";

contract X2Controller is Ownable, ReentrancyGuard {

    // using SafeMath for uint256;
    X2 public x2;
    bool public paused;

    modifier notPaused() {
        require(!paused, "Must not be paused");
        _;
    }

    constructor(
        X2 _x2
    ) public {
        x2 = _x2;
    }

    function updatePaused(bool _paused) public onlyOwner {
        paused = _paused;
    }
}
